app.layout = dl.Map(
    [
        dl.TileLayer(), 
        dl.GeoJSON(data=data), 
        dl.GeoJSON(data=markers)
    ], 
    style={
            'width': '1000px', 
            'height': '500px',
            'margin': "auto", 
            "display": "block"
        }
    )

if __name__ == '__main__':
   app.run_server(debug=True,host="0.0.0.0", dev_tools_ui=False)   